    virtual void send_custom_notifications( const double &version, const std::optional<std::string> &deviceId, const std::optional<int64_t> &accountId, const std::optional<std::string> &receiverAccountIds, const std::optional<bool> &includeFriendGroup, const std::optional<std::string> &appKey, const std::optional<std::string> &gameType, const std::optional<std::string> &conduit, const std::optional<int64_t> &contentId, const std::optional<std::string> &contentName, const std::optional<std::string> &contentType, const std::optional<int64_t> &parentId, const std::optional<std::string> &parentType, const std::optional<std::string> &actionCategory, const std::optional<std::string> &subject, const std::optional<std::string> &customMessage, const std::optional<bool> &friendOnlyAPNS, const std::optional<double> &latitude, const std::optional<double> &longitude, Pistache::Http::ResponseWriter &response) = 0;
    /// <summary>
    /// Update Notification Template
    /// </summary>
    /// <remarks>
    /// Update a notification template. Developers will only be able to update notification templates for their own applications.
    /// </remarks>
    /// <param name="version"></param>
